function getFormValues(selector) {
  var aFields = $(selector).serializeArray();
  var aReturn = [];
  $.each(aFields, function (i, item) {
    if (JSON.stringify(item).indexOf("[template]") < 0) {
      aReturn.push(item);
    }
  });
  return aReturn;
}

